import React, { useCallback, useEffect, useState } from 'react';
import useInput from '../hooks/useInput';
import styled from 'styled-components';
import { Button, Form, Input, Select } from 'antd';
import { useDispatch, useSelector } from 'react-redux';
import {  ADD_POST_REQUEST } from "../reducers/post";
import Router from 'next/router';
import { Editor } from 'react-draft-wysiwyg';
import 'react-draft-wysiwyg/dist/react-draft-wysiwyg.css';
import { EditorState,convertToRaw } from 'draft-js';
import draftToHtml from 'draftjs-to-html';

const MyBlock = styled.div`
    .wrapper-class{
        width: 50%;
        margin: 0 auto;
        margin-bottom: 4rem;
    }
  .editor {
    height: 500px !important;
    border: 1px solid #f1f1f1 !important;
    padding: 5px !important;
    border-radius: 2px !important;
  }
`;

const IntroduceContent = styled.div`
  position: relative;
  border: 0.0625rem solid #d7e2eb;
  border-radius: 0.75rem;
  overflow: hidden;
  padding: 1.5rem;
  width: 100%;
  margin: 0 auto;
  margin-bottom: 4rem;
`;

const LoginButton = styled(Button)`
margin-right : 10px;
`;

const ButtonWrapper = styled.div`
margin-top : 10px;
`
const FormWrapper = styled(Form)`
padding : 10px;
`;



const WritePostForm = () => {
    const dispatch = useDispatch();
    const { addPostLoading, addPostDone } = useSelector((state) => state.post);
    const [title, onChangeTitle] = useInput('');
    const [category, setCategory] = useState('');
    const [hashTag, onChangeHashtag] = useInput('');
    const { TextArea } = Input;
    const [content, setContent] = useState(EditorState.createEmpty());

    const onEditorStateChange = (editorState) => {
      // editorState에 값 설정
      console.log(editorState);
      setContent(editorState);
    };

    const onSubmitForm = useCallback(() => {
        dispatch({
            type: ADD_POST_REQUEST,
            data: {
                title, category, hashTag, content,
            }
        })
        setTimeout(() => { //여기 나중에 res.redirect('/')이런식으로 바꿔줘야함.
            Router.push('/');
        }, 1000);
    }, [title, category, hashTag, content]);

    const handleTeacherChange = useCallback((value) => {
        setCategory(value);
    }, [category])

    const editorToHtml = draftToHtml(convertToRaw(editorState.getCurrentContent()));
    return (

        <FormWrapper onFinish={onSubmitForm}>
            <div>
                <label htmlFor='title'>제목</label>
                <br />
                <TextArea value={title} onChange={onChangeTitle} style={{ width: "100%" }} />
            </div>
            <div>
                <label htmlFor='belong'>카테고리</label>
                <br />
                <Select style={{ width: "100%" }} onChange={handleTeacherChange}>
                    <Select.Option value="JavaScript">JavaScript</Select.Option>
                    <Select.Option value="React">React</Select.Option>
                </Select>
            </div>
            <div>
                <label htmlFor='hashtag'>해시태그</label>
                <br />
                <TextArea value={hashTag} onChange={onChangeHashtag} style={{ width: "100%" }} />
            </div>
            <div>
                <label htmlFor='content'>내용</label>
                <br />
                <MyBlock>
                    <Editor
                        // 에디터와 툴바 모두에 적용되는 클래스
                        wrapperClassName="wrapper-class"
                        // 에디터 주변에 적용된 클래스
                        editorClassName="editor"
                        // 툴바 주위에 적용된 클래스
                        toolbarClassName="toolbar-class"
                        // 툴바 설정
                        toolbar={{
                            // inDropdown: 해당 항목과 관련된 항목을 드롭다운으로 나타낼것인지
                            list: { inDropdown: true },
                            textAlign: { inDropdown: true },
                            link: { inDropdown: true },
                            history: { inDropdown: false },
                        }}
                        placeholder="내용을 작성해주세요."
                        // 한국어 설정
                        localization={{
                            locale: 'ko',
                        }}
                        // 초기값 설정
                        editorState={content}
                        // 에디터의 값이 변경될 때마다 onEditorStateChange 호출
                        onEditorStateChange={onEditorStateChange}
                    />
                    <IntroduceContent dangerouslySetInnerHTML={{__html: editorToHtml}}/>
                </MyBlock>
            </div>
            <ButtonWrapper>
                <LoginButton type="primary"
                    htmlType="submit"
                    loading={addPostLoading}>등록</LoginButton>
            </ButtonWrapper>

        </FormWrapper>

    );
}

export default WritePostForm;
