import { Card, Tag } from 'antd';
import styled from 'styled-components';
import { SiTypescript, SiJavascript } from 'react-icons/si';
import { GrReactjs } from 'react-icons/gr';
import { RiComputerFill } from 'react-icons/ri'
import { GoBrowser } from 'react-icons/go';
import { FiDatabase } from 'react-icons/fi';
//전체 post 를 가져와서 띄어주는 글 (최신순으로)

const CardWrapper = styled(Card)`
    width : 90%;
    border : 1px solid lightsteelblue;
    border-radius : 30px;
    margin : auto;
    margin-top : 10px;
`;
const PostCard = ({ post }) => {
    const { hashTag, category, id } = post;
    const hashTagSplit = [];
    if (hashTag) {

        hashTag.split(/(#[^\s#]+)/g).map((v) => {
            if (v.match(/(#[^\s#]+)/)) {
                hashTagSplit.push(v);
            }
        })

    }

    const cardTitle = [
        {
            title: (
                <>
                    <SiJavascript size="24" style={{ marginRight: "10px", color: "lightsteelblue" }} /><div style = {{display : "inline-block", fontSize : "25px"}}>{post.title}</div>
                </>
            ),
        },
        {
            title: (
                <>
                    <GrReactjs size="24" style={{ marginRight: "10px", color: "lightsteelblue" }} /><div style = {{display : "inline-block", fontSize : "25px"}}>{post.title}</div>
                </>
            ),
        },
        {
            title: (
                <>
                    <SiTypescript size="24" style={{ marginRight: "10px", color: "lightsteelblue" }} /><div style = {{display : "inline-block", fontSize : "25px"}}>{post.title}</div>
                </>
            ),
        },
        {
            title: (
                <>
                    <RiComputerFill size="24" style={{ marginRight: "10px", color: "lightsteelblue" }} /><div style = {{display : "inline-block", fontSize : "25px"}}>{post.title}</div>
                </>
            ),
        },
        {
            title: (
                <>
                    <FiDatabase size="24" style={{ marginRight: "10px", color: "lightsteelblue" }} /><div style = {{display : "inline-block", fontSize : "25px"}}>{post.title}</div>
                </>
            ),
        },
        {
            title: (
                <>
                    <GoBrowser size="24" style={{ marginRight: "10px", color: "lightsteelblue" }} /><div style = {{display : "inline-block", fontSize : "25px"}}>{post.title}</div>
                </>
            ),
        }
    ];

    let titleLogo = post;
    if (category) {
        switch (category) {
            case "JavaScript":
                titleLogo = cardTitle[0];
                break;
            case "React":
                titleLogo = cardTitle[1];
                break;
            case "TypeScript":
                titleLogo = cardTitle[2];
                break;
            case "OperatingSystem":
                titleLogo = cardTitle[3];
                break;
            case "Database":
                titleLogo = cardTitle[4];
                break;
            case "Browser":
                titleLogo = cardTitle[5];
                break;
            default : 
                break;
        }
    }



    return (
        <CardWrapper title={titleLogo.title}
            extra={<>
                <div style={{ display: "inline-block", marginRight: "5px", borderTop: "0" }}>{post.createdAt.substr(0, 10)}</div>
                <a style={{ fontSize: "19px", color: "blue" }} href={`/post/${id}`} >More</a></>} >
            <div style={{ marginBottom: "15px" }}><Tag color="geekblue"><div>Category : <a href={`category/${category}`}>{category}</a></div></Tag></div>
            {hashTag && hashTagSplit.map((value, index) => <Tag color="blue"><div>{value[0]}{value.slice(1)}</div></Tag>)}
        </CardWrapper>
    );
}

export default PostCard;
