import AppLayout from "../../components/AppLayout";
import { useRouter } from 'next/router';
import {useDispatch, useSelector} from 'react-redux';
import { loadPost } from "../../reducers/post";
import { useEffect } from "react";

//More button을 누르면, 해당 글의 id를 가지고 Post Component로 온다
//그렇다면 나는 서버로 해당 id에 해당하는 글을 가지고 와서 이를 보여주면댐.
const Post = () => {
    const router = useRouter();
    const {id} = router.query;
    const dispatch = useDispatch();
    useEffect(()=>{
        dispatch(loadPost,{id}) // 해당 id에 대한 post를 가져옴.
    });

    const { currentPost } = useSelector((state)=>state.post);
    return (
        <AppLayout>
            {currentPost && <h1>{currentPost.title}</h1>}
            <p>This is the blog post content.</p>
        </AppLayout>
    )
};

export default Post;
