import AppLayout from "../../components/AppLayout";
import { useRouter } from 'next/router';
import { useDispatch, useSelector } from 'react-redux';
import { useEffect } from "react";
import { LOAD_CURPOST_REQUEST } from "../../reducers/post";

//More button을 누르면, 해당 글의 id를 가지고 Post Component로 온다
//그렇다면 나는 서버로 해당 id에 해당하는 글을 가지고 와서 이를 보여주면댐.
const Post = () => {
    const dispatch = useDispatch();
    const router = useRouter();
    const { id } = router.query;
    const { currentPost } = useSelector((state) => state.post);

    useEffect(()=>{
        dispatch({
            type : LOAD_CURPOST_REQUEST,
            data : id,
        })
    },[])

    return (
        <AppLayout>
            <h1>{currentPost && currentPost.id}</h1>
            <h1>{currentPost &&currentPost.category}</h1>
            <h1>{currentPost &&currentPost.hashTag}</h1>
            <div>{currentPost &&urrentPost.content}</div>
        </AppLayout>
    )
};

export default Post;
