import React from 'react';
import PropTypes from 'prop-types';
import Link from 'next/link';
import { Menu,Row, Col } from 'antd';

const {SubMenu} = Menu;


const AppLayout = ({children}) => {
    return(
        <div>
        <Menu mode="horizontal">
            <Menu.Item>
                <Link href="/"><a>노드버드</a></Link>
            </Menu.Item>
            <Menu.Item>
                <Link href="/profile"><a>프로필</a></Link>
            </Menu.Item>
            <Menu.Item>
                <Link href="/signup"><a>회원가입</a></Link>
            </Menu.Item>
        </Menu>
        <Menu mode = "inline">
            <SubMenu key="sub1" title="JavaScript">
                <Menu.Item key="1">Option 1</Menu.Item>
                <Menu.Item key="2">Option 2</Menu.Item>
            </SubMenu>
            <SubMenu key="sub2" title="Nodejs">
                <Menu.Item key="1">Option 1</Menu.Item>
                <Menu.Item key="2">Option 2</Menu.Item>
            </SubMenu>
            <SubMenu key="sub3" title="React">
                <Menu.Item key="1">Option 1</Menu.Item>
                <Menu.Item key="2">Option 2</Menu.Item>
            </SubMenu>
        </Menu>
        <Row gutter = {8}>
            <Col xs ={24} md = {6}> {/* 24등분 xs 모바일 md 데스크탑*/}
            </Col>
            <Col xs ={24} md = {12}>
            {children}
            </Col>
            <Col xs ={24} md = {6}>
                <a href = "http://zerocho.com" target = "_blank" rel = "noreferrer noopenner">Made by Zerocho</a>
            </Col>
        </Row>

    </div>
    );
};

AppLayout.propTypes = {
    children: PropTypes.node.isRequired,
};

export default AppLayout;
